InstallScript:
$result = $context.ExtractArchive()
if ($result -ne 0) { return $result }
if ($context.InstallFonts(@(Get-ChildItem 'ttf\*.ttf' -ErrorAction Ignore))) { return 0 } else { return 1 }

$result = $context.ExtractArchive()
if ($result -ne 0) { return $result }
if ($context.InstallFonts(@(Get-ChildItem *.ttf -Recurse -ErrorAction Ignore))) { return 0 } else { return 1 }

$f = Join-Path $context.DownloadFolder (Split-Path $context.DownloadUrl -Leaf)
Copy-Item -LiteralPath $context.DownloadedFile -Destination $f -Force
if (Test-Path 'HKLM:\SOFTWARE\Oracle\VirtualBox\') {
    $vboxDir = (Get-ItemProperty -Path 'HKLM:\SOFTWARE\Oracle\VirtualBox\' -ErrorAction SilentlyContinue).InstallDir
    $cmd = Join-Path $vboxDir 'VBoxManage.exe'
}
if ($cmd -and (Test-Path $cmd)) {
    $context.WriteVerboseMessage("installing extensions: command `"$cmd`", file = `"$f`"")
    $blurb = 'y' | & "$cmd" extpack install --replace "$f" 2>&1
    if ($LastExitCode -ne 0) { $context.WriteWarningMessage("Extension pack installation failed with exit code ${LastExitCode}:\n${blurb}") }
    return $LastExitCode
} else {
    $context.WriteErrorMessage('could not find VBoxManage.exe; make sure VirtualBox is installed first')
    return 2
}
